public class Main {
    static int stock = 5;
    static ReentrantLock lock = new ReentrantLock();
    public static void buy(String name, int qty) {
        lock.lock();
        try {
            if (stock >= qty) { 
                stock -= qty;
                System.out.println(name + " bought " + qty);
            } else { 
                System.out.println(name + " failed");
            }
        } finally { lock.unlock(); }
    }
    public static void main(String[] args) throws InterruptedException {
        Thread t1 = new Thread(() -> buy("Ram", 3));
        Thread t2 = new Thread(() -> buy("Ravi", 4));
        t1.start(); t1.join();
        t2.start(); t2.join();
    }
}

